import React, { useState } from 'react'
import './TicTacToe.css'
import dog_icon from '../Assets/dog.png'
import cat_icon from '../Assets/cat.jpg'

let data = ["","","",
            "","","",
            "","",""]

const TicTacToe = () => {
    const [selectedSize, setSelectedSize] = useState('S');

    let[count,setCount] = useState(0);
    let[lock,setLock] = useState(false)

    const toggle = (e, num) => {
        if(lock){
            return 0;
        }
        const pieceSize = selectedSize;

    if (count % 2 === 0) {
        e.target.innerHTML = `<img src='${dog_icon}' style="width: ${selectedSize}">`;
        data[num] = "x";
        setCount(++count);
    } else {
        e.target.innerHTML = `<img src='${cat_icon}' style="width: ${selectedSize}">`;
        data[num] = "o";
        setCount(++count);
    }
    }
    return (
        <div className='container'>
        <h1 className="title">PawClaw <span>XO</span></h1>
        <div className='board'>
            <div className="row1">
                <div className="boxes" onClick={(e) => {toggle(e,0)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,1)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,2)}}></div>
            </div>
            <div className="row2">
                <div className="boxes" onClick={(e) => {toggle(e,3)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,4)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,5)}}></div>
            </div>
            <div className="row3">
                <div className="boxes" onClick={(e) => {toggle(e,6)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,7)}}></div>
                <div className="boxes" onClick={(e) => {toggle(e,8)}}></div>
            </div>

        </div>
        <div className="Size">
            <button className="PieceSizeS" onClick={() => setSelectedSize('S')}>S</button>
            <button className="PieceSizeM" onClick={() => setSelectedSize('M')}>M</button>
            <button className="PieceSizeL" onClick={() => setSelectedSize('L')}>L</button>
        </div>


        <button className='reset'>Reset</button>
        </div>
    );
}
export default TicTacToe
