import React, { useState } from 'react';
import './TicTacToe.css';
import dog_icon from '../Assets/dog.png';
import cat_icon from '../Assets/cat.jpg';

const TicTacToe = () => {
    const [selectedSize, setSelectedSize] = useState('S');
    const [count, setCount] = useState(0);
    const [lock, setLock] = useState(false);
    const [boxContent, setBoxContent] = useState(Array(9).fill(''));

    const toggle = (index) => {
        if (lock || boxContent[index] !== "") {
            return;
        }

        const pieceSize = selectedSize;
        const currentPlayer = count % 2 === 0 ? 'Dog' : 'Cat';

        const newBoxContent = [...boxContent];
        newBoxContent[index] = currentPlayer;

        setBoxContent(newBoxContent);
        setCount(count + 1);

        // Check for a winner
        const winner = calculateWinner(newBoxContent);
        if (winner) {
            alert(`${winner} wins!`);
            setLock(true);
        }
    };

    const calculateWinner = (boxes) => {
        // Logic to check for a winner
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];

        for (let i = 0; i < lines.length; i++) {
            const [a, b, c] = lines[i];
            if (boxes[a] && boxes[a] === boxes[b] && boxes[a] === boxes[c]) {
                return boxes[a];
            }
        }

        return null;
    };

    const resetGame = () => {
        setBoxContent(Array(9).fill(''));
        setCount(0);
        setLock(false);
    };

    return (
        <div className='container'>
            <h1 className="title">PawClaw <span>XO</span></h1>
            <div className='board'>
                <div className="row1">
                    {renderBox(0)}
                    {renderBox(1)}
                    {renderBox(2)}
                </div>
                <div className="row2">
                    {renderBox(3)}
                    {renderBox(4)}
                    {renderBox(5)}
                </div>
                <div className="row3">
                    {renderBox(6)}
                    {renderBox(7)}
                    {renderBox(8)}
                </div>
            </div>
            <div className="Size">
                <button className="PieceSizeS" onClick={() => setSelectedSize('S')}>S</button>
                <button className="PieceSizeM" onClick={() => setSelectedSize('M')}>M</button>
                <button className="PieceSizeL" onClick={() => setSelectedSize('L')}>L</button>
            </div>

            <button className='reset' onClick={resetGame}>Reset</button>
        </div>
    );

    function renderBox(index) {
        return (
            <div className="boxes" onClick={() => toggle(index)}>
                {boxContent[index] === 'Dog' && <img src={dog_icon} style={{ width: selectedSize }} alt="Dog" />}
                {boxContent[index] === 'Cat' && <img src={cat_icon} style={{ width: selectedSize }} alt="Cat" />}
            </div>
        );
    }
};

export default TicTacToe;
